package com.hefshine.iteration;

public class Hw13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int i,j;
			for(i=1;i<=8;i=i+2)
			{
				for(j=1;j<=i;j++) {
					System.out.print("*");
				}
				System.out.println(" ");

			}
				for(i=5;i>=1;i=i-2) {
					for(j=1;j<=i;j++) {
						System.out.print("*");

					}
					System.out.println(" ");

				}
		}

	}


	


